import { useEffect, useState } from "react";
import { string } from "prop-types";
import useInfura from "../shared/hooks/useInfura";
import LatestPrice from "../features/EthereumData/component";
import BigNumber from "bignumber.js";
import {
  ETHEREUM_ORACLE_ADDRESS,
  LINK_ORACLE_ADDRESS,
} from "../shared/abis/ropsten.oracle";
import Wrapper from "../shared/layout/component";
import { format, fromUnixTime } from "date-fns";
const timeFormat = "LLLL do HH:MM:ss";
const getLatestTimestamp = (oracle) => {
  oracle.methods.latestTimestamp().call({}, (error, res) => {
    if (error != null) {
      console.log(error);
      return;
    }
    console.log("Latest timestamp for price was:", res);
    return res;
  });
};
const formatBigInt = (number) => new BigNumber(number).shiftedBy(-8).toFixed(2);

const Index = ({ title = "NextJS IPFS Application" }) => {
  const { oracle } = useInfura({
    currentChain: process.env.INFURA_ROPSTEN,
    oracleAddress: ETHEREUM_ORACLE_ADDRESS,
  });

  const { oracle: linkOracle } = useInfura({
    currentChain: process.env.INFURA_ROPSTEN,
    oracleAddress: LINK_ORACLE_ADDRESS,
  });

  const [ethPriceData, setEthPriceData] = useState("");
  const [linkPriceData, setLinkPriceData] = useState("");

  const updateLink = () => {
    linkOracle.methods.latestAnswer().call({}, (error, res) => {
      if (error != null) {
        console.log(error);
        return;
      }
      setLinkPriceData({ ...linkPriceData, price: formatBigInt(res) });
    });

    getLatestTimestamp(linkOracle)(setLinkPriceData, linkPriceData);
  };

  const updateData = () => {
    oracle.methods.latestAnswer().call({}, (error, res) => {
      if (error != null) {
        console.log(error);
        return;
      }
      setEthPriceData({ ...ethPriceData, price: formatBigInt(res) });
    });
    getLatestTimestamp(oracle)(setEthPriceData, ethPriceData);
  };
  const getLatestTimestamp = (oracle) => (updaterFn, state) => {
    oracle.methods.latestTimestamp().call({}, (error, res) => {
      if (error != null) {
        console.log(error);
        return;
      }
      console.log({ timestamp: res });
      updaterFn({
        ...state,
        lastUpdated: format(fromUnixTime(res), timeFormat),
      });
    });
  };

  useEffect(() => {
    updateData();
    updateLink();
  }, []);
  console.log({ linkPriceData, ethPriceData });
  return (
    <Wrapper>
      <h2>{title}</h2>
      <p>
        Using{" "}
        <a target="_blank" rel="noreferrer" href="https://chain.link">
          Chain.link
        </a>{" "}
        for pricing data below.
      </p>
      <p>
        {" "}
        Learn more about Chainlink Price Feeds:{" "}
        <a
          target="_blank"
          rel="noreferrer"
          href="https://docs.chain.link/docs/using-chainlink-reference-contracts"
        >
          Chainlink Docs
        </a>
      </p>
      <p>
        {" "}
        Learn more about IPFS:{" "}
        <a target="_blank" rel="noreferrer" href="https://ipfs.io">
          IPFS.io
        </a>
      </p>
      <LatestPrice
        coin="ETH"
        price={ethPriceData.price}
        updatedAtTime={ethPriceData.lastUpdated}
        onClick={updateData}
      />
      <LatestPrice
        coin="Link"
        price={linkPriceData.price}
        updatedAtTime={linkPriceData.lastUpdated}
        onClick={updateLink}
      />
    </Wrapper>
  );
};

Index.propTypes = {
  title: string,
};

export default Index;
