import { useEffect, useState } from "react";
import useInfura from "../shared/hooks/useInfura";
import LatestPrice from "../features/EthereumData/component";
import BigNumber from "bignumber.js";
import {
  ETHEREUM_ORACLE_ADDRESS,
  LINK_ORACLE_ADDRESS,
} from "../shared/abis/ropsten.oracle";
const fromDecimal = (decimal) => (number) =>
  new BigNumber(number).shiftedBy(decimal);
const fromEth = fromDecimal(-18);

const formatNumber = (x) => new BigNumber(x).toFixed(2);

const Index = ({ title = "NextJS IPFS Application" }) => {
  const { oracle, getLatestData } = useInfura({
    currentChain: process.env.INFURA_ROPSTEN,
    oracleAddress: LINK_ORACLE_ADDRESS,
  });

  const [priceData, setPriceData] = useState("");

  console.log({ oracle });
  useEffect(() => {
    const price = getLatestData();
    setPriceData(price);
  }, []);

  return (
    <div>
      <h2>{title}</h2>
      <LatestPrice price={priceData} />
      <button onClick={getLatestData}>Update Price</button>
    </div>
  );
};

export default Index;
